mapper.select:
select 
	f_no as no,
	f_name as name,
	f_parentNo as parentNo,
	f_level as level,
	f_kind as kind
from tb_admindiv


		and f_no = #{params.no}
		and f_no is null


		and f_no in(#{item})

	and f_no >= #{params.minNo}
	and f_no < #{params.maxNo}
	and f_no like '%${params.subNo}%'
	and f_no like '%${params.startsWithNo}'
	and f_no like '${params.endsWithNo}%'

		and f_name = #{params.name}
		and f_name is null


		and f_name in(#{item})

	and f_name >= #{params.minName}
	and f_name < #{params.maxName}
	and f_name like '%${params.subName}%'
	and f_name like '%${params.startsWithName}'
	and f_name like '${params.endsWithName}%'

		and f_parentNo = #{params.parentNo}
		and f_parentNo is null


		and f_parentNo in(#{item})

	and f_parentNo >= #{params.minParentNo}
	and f_parentNo < #{params.maxParentNo}
	and f_parentNo like '%${params.subParentNo}%'
	and f_parentNo like '%${params.startsWithParentNo}'
	and f_parentNo like '${params.endsWithParentNo}%'

		and f_level = #{params.level}
		and f_level is null


		and f_level in(#{item})

	and f_level >= #{params.minLevel}
	and f_level < #{params.maxLevel}
	and f_level like '%${params.subLevel}%'
	and f_level like '%${params.startsWithLevel}'
	and f_level like '${params.endsWithLevel}%'

		and f_kind = #{params.kind}
		and f_kind is null


		and f_kind in(#{item})

	and f_kind >= #{params.minKind}
	and f_kind < #{params.maxKind}
	and f_kind like '%${params.subKind}%'
	and f_kind like '%${params.startsWithKind}'
	and f_kind like '${params.endsWithKind}%'


	order by 

		${item.name} desc


limit 0, 1

