	f_terminalSignal as terminalSignal
from tb_terminalonlinercd


		and f_id = #{params.id}
		and f_id is null


		and f_id in(#{item})

	and f_id >= #{params.minId}
	and f_id < #{params.maxId}
	and f_id like '%${params.subId}%'
	and f_id like '%${params.startsWithId}'
	and f_id like '${params.endsWithId}%'

		and f_terminalNo = #{params.terminalNo}
		and f_terminalNo is null


		and f_terminalNo in(#{item})

	and f_terminalNo >= #{params.minTerminalNo}
	and f_terminalNo < #{params.maxTerminalNo}
	and f_terminalNo like '%${params.subTerminalNo}%'
	and f_terminalNo like '%${params.startsWithTerminalNo}'
	and f_terminalNo like '${params.endsWithTerminalNo}%'

		and f_time = #{params.time}
		and f_time is null


		and f_time in(#{item})

	and f_time >= #{params.minTime}
	and f_time < #{params.maxTime}
	and f_time like '%${params.subTime}%'
	and f_time like '%${params.startsWithTime}'
	and f_time like '${params.endsWithTime}%'

		and f_state = #{params.state}
		and f_state is null


		and f_state in(#{item})

	and f_state >= #{params.minState}
	and f_state < #{params.maxState}
	and f_state like '%${params.subState}%'
	and f_state like '%${params.startsWithState}'
	and f_state like '${params.endsWithState}%'

		and f_terminalSignal = #{params.terminalSignal}
		and f_terminalSignal is null


		and f_terminalSignal in(#{item})

	and f_terminalSignal >= #{params.minTerminalSignal}
	and f_terminalSignal < #{params.maxTerminalSignal}
	and f_terminalSignal like '%${params.subTerminalSignal}%'
	and f_terminalSignal like '%${params.startsWithTerminalSignal}'
	and f_terminalSignal like '${params.endsWithTerminalSignal}%'


	order by 

		${item.name} desc



	limit ${params._start}, ${params._limit}
